c++; delay(1000); // attendre 1 seconde avant de réessayer Quitter_LCD(0); } else { // sinon, on a réussi à initialiser la carte SD Serial.println(\\"L'initialisation de la carte SD a réussi\\"); Afficher_LCD(\\"Initialisation OK\\", \\"\\", \\"\\", \\"\\", \\"\\", \\"\\", \\"\\", \\"\\"); delay(1000); Quitter_LCD(0); monFichier = SD.open(\\"data.txt\\", FILE_WRITE); // ouverture fichier \\"data\\" if (monFichier) { // Serial.print(\\"Ecriture en cours\\"); // monFichier.println(\\"Initialisation document\\"); monFichier.close(); Serial.println(\\"Ecriture réalisée\\"); Afficher_LCD(\\"Ecriture OK\\", \\"\\", \\"\\", \\"\\", \\"\\", \\"\\", \\"\\", \\"\\"); delay(1000); Quitter_LCD(0); break; // on sort de la boucle while car on a réussi à écrire dans le fichier } else { Serial.println(\\"Erreur lors de l'ouverture du fichier\\"); Afficher_LCD(\\"Erreur ouverture\\", \\"\\", \\"fichier\\", \\"\\", \\"\\", \\"\\", \\"\\", \\"\\"); delay(1000); Quitter_LCD(0); break; // on sort de la boucle while car on n'a pas réussi à ouvrir le fichier } } } } //CLOCK + ALARME **************************************************************************************************** void Dodo() { alarme_auto = false; alarme_manu = false; t = myRTC.get(); int var_hour = hour(t); int var_min = minute(t) + 1; if (var_min >= 60) { // si minute dépasse 59 var_hour++; // ajouter une heure var_min = 0; // remettre minute à zéro if (var_hour >= 24) { // si heure dépasse 23 var_hour = 0; // remettre heure à zéro } } Prochaine_alarme(var_hour, var_min); delay(100); sleepNow(); } void Horaire() { t = myRTC.get(); Serial << ((day(t) < 10) ? \\"0\\" : \\"\\") << _DEC(day(t)) << '/'; Serial << ((month(t) < 10) ? \\"0\\" : \\"\\") << _DEC(month(t)) << '/'; Serial << ((year(t) < 10) ? \\"0\\" : \\"\\") << _DEC(year(t)) << '-'; Serial << ((hour(t) < 10) ? \\"0\\" : \\"\\") << _DEC(hour(t)) << ':'; Serial << ((minute(t) < 10) ? \\"0\\" : \\"\\") << _DEC(minute(t)) << ':'; Serial << ((second(t) < 10) ? \\"0\