 // Re-evaluate the condition result = evaluateCondition(loop->condition); } } void executeCondition(Condition *condition) { // Evaluate the expression bool result = evaluateCondition(condition->expression1, condition->comparator, condition->expression2); if (result) { // Execute the statement list executeStatements(condition->statementList); } } bool evaluateCondition(Expression *expression1, Comparator *comparator, Expression *expression2) { // Get the values of the expressions void *val1 = evaluateExpression(expression1); void *val2 = evaluateExpression(expression2); // Compare the values if (strcmp(comparator->type, \\"OR\\") == 0) { return val1 || val2; } else if (strcmp(comparator->type, \\"AND\\") == 0) { return val1 && val2; } else if (strcmp(comparator->type, \\"NOT\\") == 0) { return !val1; } else if (strcmp( comparator->type, \\"EQUALS\\") == 0) { return val1 == val2; } else if (strcmp(comparator->type, \\"NOT_EQUALS\\") == 0) { return val1 != val2; } else if (strcmp(comparator->type, \\"GREATER_THAN\\") == 0) { return val1 > val2; } else if (strcmp(comparator->type, \\"LESS_THAN\\") == 0) { return val1 < val2; } else if (strcmp(comparator->type, \\"GREATER_THAN_EQUAL\\") == 0) { return val1 >= val2; } else if (strcmp(comparator->type, \\"LESS_THAN_EQUAL\\") == 0) { return val1 <= val2; } return false; } void* evaluateExpression(Expression *expression) { if (expression->expressionType == IDENTIFIER_EXP) { // Lookup the variable in the symbol table return SymbolTable_lookup(expression->identifier); } else if (expression->expressionType == NUMBER_EXP) { // Return the number return expression->number; } else if (expression->expressionType == STRING_EXP) { // Return the string return expression->string; } return NULL; } struct Expression* createExpression(int expressionType, char* identifier, int number, char* string, struct Expression* left, struct Expression* right, char* operator) { struct Expression* expr = malloc(sizeof(struct Expression)); expr->express