llRect(0, leftPaddleY, PADDLE_WIDTH, PADDLE_HEIGHT); // Draw right paddle ctx.fillStyle = \'white\'; ctx.fillRect(canvas.width - PADDLE_WIDTH, rightPaddleY, PADDLE_WIDTH, PADDLE_HEIGHT); // Draw ball ctx.beginPath(); ctx.arc(ballX, ballY, 5, 0, Math.PI * 2); ctx.fillStyle = \'white\'; ctx.fill(); } function update() { // Update ball position ballX += ballSpeedX; ballY += ballSpeedY; // Bounce ball off top and bottom walls if (ballY - 5 <= 0 || ballY + 5 >= canvas.height) { ballSpeedY = -ballSpeedY; } // Bounce ball off paddles if (ballX - 5 <= PADDLE_WIDTH && ballY > leftPaddleY && ballY < leftPaddleY + PADDLE_HEIGHT || ballX + 5 >= canvas.width - PADDLE_WIDTH && ballY > rightPaddleY && ballY < rightPaddleY + PADDLE_HEIGHT) { ballSpeedX = -ballSpeedX; } // Reset ball if it goes off screen if (ballX - 5 <= 0 || ballX + 5 >= canvas.width) { resetBall(); } // Move right paddle if (rightPaddleY + PADDLE_HEIGHT / 2 < ballY) { rightPaddleY += 3; } else { rightPaddleY -= 3; } } function resetBall() { ballX = canvas.width / 2; ballY = canvas.height / 2; ballSpeedX = -ballSpeedX; ballSpeedY = 3; } function gameLoop() { draw(); update(); requestAnimationFrame(gameLoop); } canvas.addEventListener(\'mousemove\', (e) => { let relativeY = e.clientY - canvas.offsetTop; if (relativeY > PADDLE_HEIGHT / 2 && relativeY < canvas.height - PADDLE_HEIGHT / 2) { leftPaddleY = relativeY - PADDLE_HEIGHT / 2; } }); // Start game loop gameLoop(); <\/script> </body> </html>"},{"prompt":"круто, добавь количество очков и уровни","completion":"<!DOCTYPE html> <html lang=\\"en\\"> <head> <meta charset=\\"UTF-8\\"> <meta name=\\"viewport\\" content=\\"width=device-width, initial-scale=1.0\\"> <title>Ping Pong Game</title> <style> html, body { height: 100%; margin: 0; } body { background: black; display: flex; justify-content: center; align-items: center; font-family: Arial, Helvetica, sans-serif; } canvas { border: 1px solid white; } </style> </head> <body> <canvas id=\\"gameCanvas\\" width=\\"800\\