ded = false; // Start a new game startGame(); } function startGame() { document.getElementById(\\"startBtn\\").style.display = \\"none\\"; const gameBoard = document.getElementById('game-board'); gameBoard.innerHTML = ''; for (let row = 0; row < boardHeight; row++) { const divRow = document.createElement('div'); divRow.classList.add('row'); for (let col = 0; col < boardWidth; col++) { const divRectangle = document.createElement('div'); divRectangle.classList.add('rectangle'); divRectangle.addEventListener(\\"click\\", pickRectangle); if (row < numFilledRows) { divRectangle.style.backgroundColor = getRandomColor(); divRectangle.classList.add('visible'); } divRow.appendChild(divRectangle); } gameBoard.appendChild(divRow); } // Create a new HTML element to display the score const scoreElement = document.getElementById(\\"score\\"); scoreElement.innerHTML = 'Score: 0'; // Initialize score value in the element backgroundMusic.play(); backgroundMusic.loop = true; setTimerAndAddRow(); } function getRandomColor() { const colors = ['red', 'blue', 'green', 'yellow', 'purple', 'orange']; const randomIndex = Math.floor(Math.random() * colors.length); return colors[randomIndex]; } let click = 0; let draggedRectangles = []; let numberOfRectangles = 0; let rectangleColor = null; let i = 0; let pickedUpRectangles = 0; function pickRectangle(event) { if (click === 0) { const gameBoard = document.getElementById(\\"game-board\\"); const boardRect = gameBoard.getBoundingClientRect(); const columnWidth = gameBoard.offsetWidth / boardWidth; const columnIndex = Math.floor((event.clientX - boardRect.left) / columnWidth); draggedRectangles.push(...getColoredRectangles(columnIndex)); // Find the bottom-most empty row in that column const rectangles = document.getElementsByClassName(\\"rectangle\\"); rectangleColor = draggedRectangles[0].style.backgroundColor; numberOfRectangles = draggedRectangles.length; pickedUpRectangles = draggedRectangles.length; draggedRectangles.forEach(rectangle => {